targets.expr:
sum_over_time(
    (
        sum(
            (increase(
                hass_sensor_energy_kwh{entity="sensor.shellyem_main_channel_1_energy"}[1h]
            )
            * scalar(
                hass_sensor_unit_u0x20ac_per_kwh{entity="sensor.esios_pvpc"}
            ))
            >= 0
        ) by (entity, friendly_name)
    )[$__range:1h]
) / sum(increase(hass_sensor_energy_kwh{entity="sensor.shellyem_main_channel_1_energy"}[$__range])) by (entity, friendly_name)
